import React, { useState } from 'react';
import ReactDOM from 'react-dom';
function App() {
    const [email, setEmail] = useState('');
    const [contrasenia, setContrasenia] = useState('');
    const [mensaje, setMensaje] = useState('');
  
    const cabeceraLogin = async () => {
      const respuesta = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, contrasenia }),
      });
      const data = await respuesta.json();
      setMensaje(data.message || data.token || 'Error en el servidor');
    };
    const cabeceraRegistro = async () => {
        const respuesta = await fetch('/api/auth/register', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email, contrasenia }),
        });
        const data = await respuesta.json();
        setMensaje(data.message || 'Error en el servidor');
      };
      return (
        <div>
          <h2>Registro e Inicio de Sesi칩n</h2>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Correo electr칩nico"
          />
          <input
            type="password"
            value={contrasenia}
            onChange={(e) => setContrasenia(e.target.value)}
            placeholder="Contrase침a"
          />
          <button onClick={cabeceraRegistro}>Registrar</button>
          <button onClick={cabeceraLogin}>Iniciar sesi칩n</button>
          <div>{mensaje}</div>
        </div>
      );
}
